mapper.insert:
insert into TOM_COURSE_LEARNING_RECORD (COURSE_ID, CODE, LEARNING_DATE
  )
values (#{courseId,jdbcType=DECIMAL}, #{code,jdbcType=VARCHAR}, #{learningDate,jdbcType=TIMESTAMP}
  )

mapper.select:
   select count(1) from TOM_COURSE_LEARNING_RECORD c 
	where 1=1

        and COURSE_ID = #{courseId,jdbcType=DECIMAL} 


       and CODE = #{code,jdbcType=VARCHAR} 


        and #{learningDate,jdbcType=TIMESTAMP} >=LEARNING_DATE

	select * from TOM_COURSE_LEARNING_RECORD WHERE 1=1

      and COURSE_ID = #{courseId,jdbcType=DECIMAL} 


     and CODE = #{code,jdbcType=VARCHAR} 


      and #{learningDate,jdbcType=TIMESTAMP} >=LEARNING_DATE

select count(1) from TOM_COURSE_LEARNING_RECORD a ,TOM_ACTIVITY_EMPS_RELATION b where a.code=b.code and b.apply_status='Y'

     and a.COURSE_ID = #{courseId,jdbcType=DECIMAL} 


     and b.ACTIVITY_ID = #{activityId,jdbcType=DECIMAL} 


     and #{learningDate,jdbcType=TIMESTAMP} >=LEARNING_DATE

mapper.delete:

delete from TOM_COURSE_LEARNING_RECORD
where 1=1

    and COURSE_ID = #{courseId,jdbcType=DECIMAL} 


   and CODE = #{code,jdbcType=VARCHAR} 


    and #{learningDate,jdbcType=TIMESTAMP} >=LEARNING_DATE

mapper.select:

	select * from TOM_COURSE_LEARNING_RECORD WHERE 1=1

      and COURSE_ID = #{courseId,jdbcType=DECIMAL} 


     and CODE = #{code,jdbcType=VARCHAR} 


      and #{learningDate,jdbcType=TIMESTAMP} >=LEARNING_DATE 
    order by LEARNING_DATE  desc 
  limit
  #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}
